import React,{useState} from 'react'
import { Layout, Menu } from 'antd';
import {
    MenuUnfoldOutlined,
    MenuFoldOutlined,
    UserOutlined,
    VideoCameraOutlined,
    UploadOutlined,
} from '@ant-design/icons';
const { Header, Sider, Content } = Layout;
import '../'
function AdminArea() {
    const [slider, setSlider] = useState(false);
    // const state = {
    //     collapsed: false,
    // };

    // const toggle = () => {
    //     this.setState({
    //         collapsed: !this.state.collapsed,
    //     });
    // };

    const toggle = () => {
        console.log(!slider);
        setSlider(!slider);
    }

    return (
        <>
        <Sider trigger={null} collapsible collapsed={slider}>
          
        
          <Menu theme="dark" mode="inline" defaultSelectedKeys={['1']}>
            <Menu.Item key="1" icon={<UserOutlined />}>
              nav 1
            </Menu.Item>
            <Menu.Item key="2" icon={<VideoCameraOutlined />}>
              nav 2
            </Menu.Item>
            <Menu.Item key="3" icon={<UploadOutlined />}>
              nav 3
            </Menu.Item>
          </Menu>
            {React.createElement( slider ? MenuUnfoldOutlined : MenuFoldOutlined, {
              className: 'trigger',
              onClick: toggle,
              
            })}
        </Sider> 
        </>
    )
}

export default AdminArea
