import { useState, useEffect } from 'react';
import { projectStorage } from '../firebase';

const useStorage = (filePhoto, fileSign, fileIdProof) => {
    const [progress, setProgress] = useState(0);
    const [error, setError] = useState(null);
    const [url, setUrl] = useState(null);

    useEffect(() => {
        // references
        console.log("use storeage called ")
        //   console.log("use  ",filePhoto)
        if (filePhoto) {
            filePhoto.name ="abcxyz"
            const storageRef = projectStorage.ref(filePhoto.name);
            // //   const collectionRef = projectFirestore.collection('images');

              storageRef.put(filePhoto).on('state_changed', (snap) => {
                let percentage = (snap.bytesTransferred / snap.totalBytes) * 100;
                setProgress(percentage);
              }, (err) => {
                setError(err);
              }, async () => {
                const url = await storageRef.getDownloadURL();
            //     // const createdAt = timestamp();
            //     // await collectionRef.add({ url, createdAt });
                setUrl(url);
              });
            console.log("changes made");
        }

    }, [submit]);
    console.log(progress, url);
    return { progress, url, error };

}


export default useStorage;
