import React , { useState } from 'react'
import {
    Form,
    Select,
    InputNumber,
    
    Radio,
   
    Button,
    Upload,
    
    Input,
    DatePicker
} from 'antd';
import { UploadOutlined, InboxOutlined } from '@ant-design/icons';
import moment from 'moment';
import '../../custom.css'
import country from 'country_state_district';

const states = country.getStatesByCountryId(1);
const dateFormat = 'YYYY/MM/DD';


const { Option } = Select;
const formItemLayout = {
    labelCol: { span: 3 },
    wrapperCol: { span: 16 },
    labelAlign: 'left'
};

const normFile = (e) => {
    console.log('Upload event:', e);
    if (Array.isArray(e)) {
        return e;
    }
    return e && e.fileList;
};


function panCardForm() {
    function onChange(value) {
        console.log('changed', value);
      }
      
    const onFinish = (values) => {
        console.log('Success:', values);
    };

    const onFinishFailed = (errorInfo) => {
        console.log('Failed:', errorInfo);
    };



    return (
        <>
            <h1>PanCard Form</h1>
            <Form
                name="validate_other"
                {...formItemLayout}
                onFinish={onFinish}
                initialValues={{
                    'input-number': 3,
                    rate: 3.5,
                }}
            >
            <Form.Item name="nationality" label="Nationality" rules={[{ required: true, message: 'Please select nationality' }]}  style={{textAlign:"left"}}>
                    <Radio.Group>
                        <Radio value="Indian">Indian Resident</Radio>
                        <Radio value="Nri">Non Resident Indian</Radio>
                    </Radio.Group>
                </Form.Item>


            </Form>
        </>
    )
}

export default panCardForm
