import React, { useState } from 'react'
import {
    Form,
    Select,
    InputNumber,

    Radio,

    Button,
    Upload,

    Input,
    DatePicker
} from 'antd';
import { UploadOutlined, InboxOutlined } from '@ant-design/icons';
import moment from 'moment';
import '../../custom.css'
import country from 'country_state_district';

const states = country.getStatesByCountryId(1);
const dateFormat = 'YYYY/MM/DD';


const { Option } = Select;
const formItemLayout = {
    labelCol: { span: 6 },
    wrapperCol: { span: 16 },
    labelAlign: 'left'
};

const normFile = (e) => {
    console.log('Upload event:', e);
    if (Array.isArray(e)) {
        return e;
    }
    return e && e.fileList;
};
function VoterIdForm() {

    function onChange(value) {
        console.log('changed', value);
    }

    const onFinish = (values) => {
        console.log('Success:', values);
    };

    const onFinishFailed = (errorInfo) => {
        console.log('Failed:', errorInfo);
    };
    return (
        <>
            <h1>Voter Id form</h1>
            <Form
                name="validate_other"
                {...formItemLayout}
                onFinish={onFinish}
                initialValues={{
                    'input-number': 3,
                    rate: 3.5,
                }}
            >
                <Form.Item name="enrolment-type" label="Enrolment Type" style={{ textAlign: "left" }} rules={[{ required: true, message: 'Please select enrolment type' }]}>
                    <Radio.Group>
                        <Radio value="new" >First Time Voter</Radio>
                        <Radio value="update">shifting from another constituency</Radio>
                    </Radio.Group>
                </Form.Item>



                <Form.Item name="name" style={{ textAlign: "left" }} label="Name" rules={[{ required: true, message: 'Please enter  Name!' }]}>
                    <Input />
                </Form.Item>


                <Form.Item name="surname" style={{ textAlign: "left" }} label="Surname" rules={[{ required: true, message: 'Please enter  surname!' }]}>
                    <Input />
                </Form.Item>
                <Form.Item name="relative-name" style={{ textAlign: "left" }} label="Name & surname of a Relative" rules={[{ required: true, message: 'Please enter  surname!' }]}>
                    <Input />
                </Form.Item>

                <Form.Item name="relation-type" label="" rules={[{ required: true, message: 'Please select applicantStatus' }]} style={{ textAlign: "left" }}>
                    <Radio.Group>
                        <Radio value="individual" onClick={() => setIndividual(true)}>Individual</Radio>
                        <Radio value="trusts">Trusts</Radio>

                        <Radio value="huf">Hindu Undivided family</Radio>
                        <Radio value="b-individual">Body of individual</Radio>

                        <Radio value="company">Compny</Radio>
                        <Radio value="local-authority">Local authority</Radio>

                        <Radio value="partership">Partership firm</Radio>
                        <Radio value="a-judicial-person">Artificial judicial person</Radio>

                        <Radio value="govt">Goveronment</Radio>
                        <Radio value="association">Association Of person</Radio>

                        <Radio value="llp">Limited liablity partnership</Radio>


                    </Radio.Group>
                </Form.Item>
            </Form>
        </>
    )
}

export default VoterIdForm
