import React from 'react'
import { Form, Input, Button, Checkbox } from 'antd';
import { UserOutlined, LockOutlined } from '@ant-design/icons';
import '../custom.css'

const credential = [
     {
        type:"aadharAdmin",
        username: "aadharAdmin",
        password: "aadharPassword"
    },
    {
        type:"panAdmin",
        username: "panAdmin",
        password: "panPassword"
    },
    {
        type:"voterIdAdmin",
        username: "voterIdAdmin",
        password: "voterIdPassword"
    },
    {
        type:"super",
        username: "super",
        password: "super"
    },
    {
        type:"admin",
        username: "admin",
        password: "admin"
    }
]


function filter(array, value, key) {
    return array.filter(key
        ? credential => credential[key] === value
        : credential => Object.keys(credential).some(k => credential[k] === value)
    );
}
function Login() {

    const onFinish = (values) => {
        // console.log('Received values of form: ', values);
        
        console.log(filter(credential, values.username, 'username'));
        let result = filter(credential, values.username, 'username')[0];
        console.log(result.username  === values.username);
        console.log(result.username  === values.username);
        if (result.username === values.username && result.password === values.password) {
            console.log("pass");
        } else {
            console.log("fail");
        }
    };

    return (
        <div className="login-container">
            <h1>Login Here</h1>
            <Form
                // style={{padding:'0 5rem 0 5rem'}}
                name="normal_login"
                className="login-form"
                initialValues={{ remember: true }}
                onFinish={onFinish}
            >
                <Form.Item
                    name="username"
                    rules={[{ required: true, message: 'Please input your Username!' }]}
                >
                    <Input prefix={<UserOutlined className="site-form-item-icon" />} placeholder="Username" />
                </Form.Item>
                <Form.Item
                    name="password"
                    rules={[{ required: true, message: 'Please input your Password!' }]}
                >
                    <Input
                        prefix={<LockOutlined className="site-form-item-icon" />}
                        type="password"
                        placeholder="Password"
                    />
                </Form.Item>
                <Form.Item>
                    <Form.Item name="remember" valuePropName="checked" noStyle>
                        <Checkbox>Remember me</Checkbox>
                    </Form.Item>
                </Form.Item>

                <Form.Item>
                    <Button type="primary" htmlType="submit" className="login-form-button">
                        Log in
        </Button>

                </Form.Item>
            </Form>
        </div>
    )
}

export default Login
