import React , { useState } from 'react'
import {
    Form,
    Select,
    InputNumber,
    Switch,
    Radio,
    Slider,
    Button,
    Upload,
    Rate,
    Row,
    Col,
    Input
} from 'antd';
import { UploadOutlined, InboxOutlined } from '@ant-design/icons';

import './custom.css'

const { Option } = Select;
const formItemLayout = {
    labelCol: { span: 3 },
    wrapperCol: { span: 16 },
    labelAlign: 'left'
};

const normFile = (e) => {
    console.log('Upload event:', e);
    if (Array.isArray(e)) {
        return e;
    }
    return e && e.fileList;
};
function AadharCardForm() {
   
    const [aadharNoInput, setaadharNoInput] = useState(false)
    const toggleAadharNoInput = (e) =>{
        if(e.target.value === "update")
        {
            setaadharNoInput(true);
        }
        else{
            setaadharNoInput(false);
        }
        
    }


    function onChange(value) {
        console.log('changed', value);
      }
      
    const onFinish = (values) => {
        console.log('Success:', values);
    };

    const onFinishFailed = (errorInfo) => {
        console.log('Failed:', errorInfo);
    };


    return (
        <>
            <Form
                name="validate_other"
                {...formItemLayout}
                onFinish={onFinish}
                initialValues={{
                    'input-number': 3,
                    rate: 3.5,
                }}
            >
               
               <Form.Item name="nationality" label="Nationality" rules={[{ required: true, message: 'Please select nationality' }]}  style={{textAlign:"left"}}>
                    <Radio.Group>
                        <Radio value="Indian">Indian Resident</Radio>
                        <Radio value="Nri">Non Resident Indian</Radio>
                    </Radio.Group>
                </Form.Item>

                <Form.Item name="enrolment-type" label="Enrolment Type"  style={{textAlign:"left"}} rules={[{ required: true, message: 'Please select enrolment type' }]}>
                    <Radio.Group>
                        <Radio value="new" onChange={(e)=> toggleAadharNoInput(e)}>New Enrolment</Radio>
                        <Radio value="update" onChange={(e)=> toggleAadharNoInput(e)}>Update Request</Radio>
                    </Radio.Group>
                </Form.Item>
            
                {aadharNoInput ?(
                    <>
                    <Form.Item  label="Aadhar No" labelCol={ { span: 3 }}
                     style={{textAlign:"left"}} >
                    <Form.Item name="aadhar-number"
                    
                    rules={[{ required: true, message: 'Please enter Aadhar Number!' }]}>
                    <InputNumber  style={{ width: 300 }} maxlength="12" className="aadhar-number" onChange={onChange}/>
                    </Form.Item>
                    
                </Form.Item>
                   
                </>
                ):(<></>)}
                <Form.Item name="fullName" style={{textAlign:"left"}}  label="Full Name" rules={[{ required: true, message: 'Please enter Name!' }]}>
                    <Input />
                </Form.Item>
                <Form.Item name="relationOf" style={{textAlign:"left"}}  label="C/O" rules={[{ required: true, message: 'Please enter Name!' }]}>
                    <Input />
                </Form.Item>
                <Form.Item
                    name="select"
                    label="Select"
                    hasFeedback
                    rules={[{ required: true, message: 'Please select your country!' }]}
                    style={{textAlign:"left"}}
                >
                    <Select placeholder="Please select a country">
                        <Option value="china">China</Option>
                        <Option value="usa">U.S.A</Option>
                    </Select>
                </Form.Item>

                <Form.Item
                    name="select-multiple"
                    label="Select[multiple]"
                    rules={[{ required: true, message: 'Please select your favourite colors!', type: 'array' }]}
                    style={{textAlign:"left"}}
                >
                    <Select mode="multiple" placeholder="Please select favourite colors">
                        <Option value="red">Red</Option>
                        <Option value="green">Green</Option>
                        <Option value="blue">Blue</Option>
                    </Select>
                </Form.Item>

                <Form.Item label="InputNumber"  style={{textAlign:"left"}} >
                    <Form.Item name="input-number" noStyle>
                        <InputNumber min={1} max={10} />
                    </Form.Item>
                    <span className="ant-form-text"> machines</span>
                </Form.Item>

                <Form.Item name="switch" label="Switch" valuePropName="checked"  style={{textAlign:"left"}}>
                    <Switch />
                </Form.Item>

                <Form.Item name="slider" label="Slider"  style={{textAlign:"left"}}>
                    <Slider
                        marks={{
                            0: 'A',
                            20: 'B',
                            40: 'C',
                            60: 'D',
                            80: 'E',
                            100: 'F',
                        }}
                    />
                </Form.Item>

                <Form.Item name="radio-group" label="Radio.Group"  style={{textAlign:"left"}}>
                    <Radio.Group>
                        <Radio value="a">item 1</Radio>
                        <Radio value="b">item 2</Radio>
                        <Radio value="c">item 3</Radio>
                    </Radio.Group>
                </Form.Item>

                <Form.Item
                    name="radio-button"
                    label="Radio.Button"
                    rules={[{ required: true, message: 'Please pick an item!' }]}
                    style={{textAlign:"left"}}
                >
                    <Radio.Group>
                        <Radio.Button value="a">item 1</Radio.Button>
                        <Radio.Button value="b">item 2</Radio.Button>
                        <Radio.Button value="c">item 3</Radio.Button>
                    </Radio.Group>
                </Form.Item>

               

                <Form.Item name="rate" label="Rate"  style={{textAlign:"left"}} >
                    <Rate />
                </Form.Item>

                <Form.Item
                    name="upload"
                    label="Upload"
                    valuePropName="fileList"
                    getValueFromEvent={normFile}
                    extra="longgggggggggggggggggggggggggggggggggg"
                    style={{textAlign:"left"}}
                >
                    <Upload name="logo" action="/upload.do" listType="picture">
                        <Button icon={<UploadOutlined />}>Click to upload</Button>
                    </Upload>
                </Form.Item>

                <Form.Item label="Dragger"  style={{textAlign:"left"}}>
                    <Form.Item name="dragger" valuePropName="fileList" getValueFromEvent={normFile} noStyle>
                        <Upload.Dragger name="files" action="/upload.do">
                            <p className="ant-upload-drag-icon">
                                <InboxOutlined />
                            </p>
                            <p className="ant-upload-text">Click or drag file to this area to upload</p>
                            <p className="ant-upload-hint">Support for a single or bulk upload.</p>
                        </Upload.Dragger>
                    </Form.Item>
                </Form.Item>

                <Form.Item wrapperCol={{ span: 12, offset: 6 }} style={{textAlign:"left"}} >
                    <Button type="primary" htmlType="submit">
                        Submit
        </Button>
                </Form.Item>
            </Form>

        </>
    )
}

export default AadharCardForm
